mapper.select:
SELECT UUID,DEPT,TEAM,JOBNUMBER,NAME,DATE_FORMAT(CTIME,'%Y-%m-%d %H:%i:%s') CTIME FROM XS_PX


         DEPT = #{dept}


         AND TEAM like #{team}


         AND JOBNUMBER like #{jobNumber}


         AND NAME = #{name}


         AND CTIME >= #{startDate}


         AND CTIME <= #{endDate}


 ORDER BY 'CTIME' DESC
 LIMIT #{page},#{limit}

SELECT UUID,DEPT,TEAM,JOBNUMBER,NAME,DATE_FORMAT(CTIME,'%Y-%m-%d %H:%i:%s') CTIME FROM XS_PX


        DEPT = #{dept}


        AND TEAM like #{team}


        AND JOBNUMBER like #{jobNumber}


        AND NAME = #{name}


        AND CTIME >= STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')


        AND CTIME <= STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')


ORDER BY 'CTIME' DESC

SELECT COUNT(UUID) COUNT FROM XS_PX


        DEPT = #{dept}


        AND TEAM like #{team}


        AND JOBNUMBER like #{jobNumber}


        AND NAME = #{name}


        AND CTIME >= STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')


        AND CTIME <= STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')
